import axios from "axios"

export const API_URL = "https://localhost:7055";

const $api = axios.create({
    baseURL: API_URL,
    withCredentials: true,
});

$api.interceptors.request.use((config) => {
    config.headers.Authorization = `Bearer ${localStorage.getItem('aToken')}`
    return config;
});

$api.interceptors.response.use((config) => {
    config.headers.Authorization = `Bearer ${localStorage.getItem('rToken')}`
    return config;
}, async (error) =>  {
    const originalRequest = error.config;
    console.log(originalRequest);
    if(error.response.status == 401) {
        try {
            const response = await $api.get(`${API_URL}/Token/Refresh`, { withCredentials: true});
            localStorage.setItem('token', response.data.aToken);
            return $api.request(originalRequest);
        }  catch (e) {
            console.log('не авторизован');
        }
    }
});

export default $api;
