import React, { useContext, useEffect, useState } from 'react';
import './Settings.css';
import { Context } from '../..';

function Settings() {


  const {store} = useContext(Context);

  const [userName, setUserName] = useState(null);
  const [userSurname, setUserSurname] = useState(null);
  const [userDescription, setUserDescription] = useState(null);
  const [userImage, setUserImage] = useState(null);
  const [userBannerImage, setUserBannerImage] = useState(null);
  const [userTypeMBTI, setUserTypeMBTI] = useState(null);

  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchUserData = async () => {
      setIsLoading(true);
      const userId = localStorage.userId; 
      

      try {
        const userInfo = await store.userInfo(userId);
        setUserName(userInfo.name);
        setUserSurname(userInfo.surname);
        setUserDescription(userInfo.description);
        setUserImage(userInfo.image);
        setUserBannerImage(userInfo.bannerImage);
        setUserTypeMBTI(userInfo.typeMbti);
      } catch (error) {
          console.error("Failed to fetch user data:", error);
      } finally {
          setIsLoading(false);
      }
    };

    fetchUserData();
    
  }, [])

  if (isLoading) {
    return <></>;
}

  return (
    <div className='settings__wrapper'>
      {isModalShow && 
        <div className='modal--bg'>
        <form onSubmit={onSubmitPost} className='profile__modal modal'>
        {(validText) && <span className='profile__post--error'><p>Вы не заполнили публикацию</p></span>}
          <div className='modal__header'>
            <h2 className='modal__heading'>Что у вас нового?</h2>
            <span className='modal__description'>Поделитесь с друзьями!</span>
            <button onClick={hideModel} type="button" className='modal__button--close'><CloseSvg className='modal__icon'/></button>
          </div>
          <div className='modal__body'>
            <img className='modal__avatar' src={userImage ? userImage : Placeholder}/>
            <textarea onChange={(e) => setNewPost(e.target.value)} className='modal__input' placeholder='Расскажите нам!'/>
          </div>
          <div className='modal__photos'>
            <label className='modal__photo'>Хотите добавить фото?<input multiple onChange={(e) => setNewPostImages(Object.values(e.target.files))} ref={inputRef} type="file" className='modal__input--image'/></label>
          </div>
          <button type="submit" className='modal__button'>Опубликовать пост</button>
        </form>
        </div>
      }
      <form className="settings__main" autoComplete="off">
        <section className="settings__bg">
          <img className="settings__bg--img" src={userBannerImage}/>
          <span className="settings__bg--text">COGNI</span>
          <span className="settings__bg-edit">Изменить обложку</span>
        </section>
        <section className="settings__human human">
          <div className="human__left">
            <span className="human__avatar-border"><img src={userImage} alt=" " className="human__avatar"/><span className="human__avatar-edit">+</span></span>
            <span className="human__mbti"></span>
          </div>
          <div className="human__right">
            <h1 className='human__name'>{userName + " " + userSurname }<span className='human__name-edit'>Изменить</span></h1>
            <p className='human__description'><span className='human__description-text'>{userDescription}</span><span className='human__description-edit'>Изменить</span></p>
          </div>
        </section>
        <section className='settings__info'>
          <div className="settings__type">
            <h2 className="settings__type-heading">Изменить тип личность</h2>
            <div>
            <p className="settings__type-mbti">
              <span className="mbti-letter">{userTypeMBTI[0]}</span>
              <span className="mbti-letter">{userTypeMBTI[1]}</span>
              <span className="mbti-letter">{userTypeMBTI[2]}</span>
              <span className="mbti-letter">{userTypeMBTI[3]}</span>
            </p>
            <button className="settings__type-button">Пройти тест заново</button>
            </div>
          </div>
          <div className="settings__password">
            <h2 className="settings__password-heading">Изменить пароль</h2>
            <div className="settings__password-edit">
              <input className="settings__password-input" type="password" placeholder="Новый пароль"></input>
              <input className="settings__password-input" type="password" placeholder="Старый пароль"></input>
            </div>
          </div>
          <button className="settings__save-button" type="submit">Сохранить изменения</button>
        </section>
      </form>
    </div>
  );
};

export default Settings;
