import React, { useContext, useEffect, useState } from 'react';
import './Settings.css';
import { Context } from '../..';

function Settings() {


  const {store} = useContext(Context);

  const [userName, setUserName] = useState(null);
  const [userSurname, setUserSurname] = useState(null);
  const [userDescription, setUserDescription] = useState(null);
  const [userImage, setUserImage] = useState(null);

  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchUserData = async () => {
      setIsLoading(true);
      const userId = localStorage.userId; 
      

      try {
        const userInfo = await store.userInfo(userId);
        setUserName(userInfo.name);
        setUserSurname(userInfo.surname);
        setUserDescription(userInfo.description);
        setUserImage(userInfo.image);
      } catch (error) {
          console.error("Failed to fetch user data:", error);
      } finally {
          setIsLoading(false);
      }
    };

    fetchUserData();
    
  }, [])

  if (isLoading) {
    return <></>;
}

  return (
    <div className='profile__wrapper'>
      <div className="profile__main">
        <section className="profile__bg">
          <img className="profile__bg--img" />
        </section>
        <section className="profile__human human">
          <div className="human__left">
            <img  className="human__avatar"/>
            <span className="human__mbti"></span>
          </div>
          <div className="human__right">
            <h1 className='human__name'></h1>
            <p className='human__description'></p>
          </div>
        </section>
      </div>
    </div>
  );
};

export default Settings;
