import axios from "axios"

export const createUser = async (user) => {
    try {
        var response = await axios.post("https://localhost:7055/User/CreateUser", user);

        return response.status;
    } catch(e) {
        console.error(e);
    }
}

//Валидация
export const isFormValid = (user) => {
    var error = {status: true, name: null, surname: null, email: null, password: null};

    if (user?.name[0] !== user?.name[0].toUpperCase() || user?.name.length <= 1) {
        error.name = "Имя должно начинаться с заглавной буквы и содержать минимум 2 символа";
        error.status = false;
    }
    if (user?.surname[0] !== user?.surname[0].toUpperCase() || user?.surname.length <= 1) {
        error.surname = "Фамилия должна начинаться с заглавной буквы и содержать минимум 2 символа";
        error.status = false;
    }
    if (!validateEmail(user?.email)) {
        error.surname = "Email указан не верно";
        error.status = false;
    }
    if (!validatePassword(user?.password)) {
        error.surname = "Пароль должен состоять минимум из 8 символов и содержать хотя бы один из символов: !@#$%^&*";
        error.status = false;
    }
    return error;
};


//Валидация email
const validateEmail = (email) => {
    return email.match(
      /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
    );
  };

//Валидация password
const validatePassword = (password) => {
    var re = /^(?=.*\d)(?=.*[!@#$%^&*])(?=.*[a-z])(?=.*[A-Z]).{8,}$/;
    return re.test(password);
  };
