import React, { useContext, useEffect, useState } from "react";
import './Wiki.css';
import { ReactComponent as EditIcon } from './img/edit_article.svg';
import { useNavigate } from 'react-router-dom';
import { Context } from '../..';

function WikiArticle() {

    useEffect(() => {
    
        const fetchArticles = async () => {
        setIsLoading(true);
          
    
          try {
            const articlesDownload = await store.getArticles();
            setArticles(articlesDownload);
          } catch (error) {
              console.error("Failed to fetch user posts:", error);
          } finally {
            setIsLoading(false);
        }
        };
    
        fetchArticles();
      }, [])

    return (
        <div className="wiki">
           <div className="wiki__wrapper">
                <section className="wiki__main">
                    <h1 className="wiki__heading">ВИКИ «Cogni» <span className="wiki__icon"><EditIcon/></span></h1>
                    <div className="wiki__write">
                        <p className="read-article__heading" placeholder="Название статьи"></p>
                        <p className="read-article__text" placeholder="Текст статьи"></p>
                    </div>
                </section>

                <section className="wiki__types types">
                    <h3 className="types__heading">Ваши статьи</h3>
                    <ul className="types__list">
                    </ul>
                </section>
           </div>
        </div>
    );
};

export default WikiArticle;
