import axios from "axios"

export const createUser = async (user) => {
    try {
        var response = await axios.post("https://localhost:7055/User/CreateUser", user);

        return response.status;
    } catch(e) {
        console.error(e);
    }
}

//Валидация
export const isFormValid = (user) => {
    if (user?.name[0] !== user?.name[0].toUpperCase() || user?.name.length <= 1) {
        return false;
    } else if (user?.surname[0] !== user?.surname[0].toUpperCase() || user?.surname.length <= 1) {
        return false;
    } else if (!validateEmail(user?.email)) {
        console.log('error');
        return false;
    } else if (!validatePassword(user?.password)) {
        
        return false;
    } else {
        return true;
    }
};


//Валидация email
const validateEmail = (email) => {
    return email.match(
      /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
    );
  };

//Валидация password
const validatePassword = (password) => {
    var re = /^(?=.*\d)(?=.*[!@#$%^&*])(?=.*[a-z])(?=.*[A-Z]).{8,}$/;
    return re.test(password);
  };
