import { useContext } from "react";
import { Context } from "..";
import $api from "../http"

export const getUserById = async (userId) => {
    try {
        let response = await $api.get("/User/GetUserById", { params: { id: userId } });
        return response;
    } catch(e) {
        console.log(e);
    }
}

const {store} = useContext(Context);

export const getUserName = async (userId) => {
    try {
      let response = await store.userInfo(userId);
      return response.name;
    } catch(e) {
      console.error(e);
    }
  };

export const getUserSurname = async (userId) => {
    try {
      let response = await store.userInfo(userId);
      return response.surname;
    } catch(e) {
      console.error(e);
    }
  };

export const getUserDescription = async (userId) => {
    try {
      let response = await store.userInfo(userId);
      return response.description;
    } catch(e) {
      console.error(e);
    }
  };
