import axios from "axios"

export const API_URL = "https://localhost:7055";

const $api = axios.create({
    baseURL: API_URL,
    withCredentials: true,
});

$api.interceptors.request.use((config) => {
    config.headers.Authorization = `Bearer ${localStorage.getItem('aToken')}`
    return config;
});

$api.interceptors.response.use((config) => {
    return config;
}, async (error) => {
    const originalRequest = error.config;
    if (error.response.status == 401) {
        localStorage.removeItem('aToken');

        const rToken = localStorage.getItem('rToken');
        const userId = localStorage.getItem('userId');
        if (rToken) {
            try {
                const refreshResponse = await axios.post(`${API_URL}/Token/Refresh`, { token: rToken, id: userId });
                localStorage.setItem('aToken', refreshResponse.data.aToken);
                error.config.headers.Authorization = `Bearer ${refreshResponse.data.aToken}`;
                return $api.request(error.config);
            } catch (e) {
                console.error("Не удалось обновить токен. Переход на логин.");
            }
        }
    }
    console.log(error.config);
});

export const getUserById = async (userId) => {
    try {
        let response = await $api.get("/User/GetUserById", { params: { id: userId } });
        return response;
    } catch(e) {
        console.error(1);
    }
}
