import React, { useContext, useEffect, useRef, useState } from 'react';
import './Friends.css';
import { Context } from '../..';
import { Link, useNavigate, useParams } from 'react-router-dom';

function Frineds() {

  const navigate = useNavigate()

  let params = useParams()

  console.log(params);
  
  const {store} = useContext(Context);

  const [userId, setUserId] = useState(null);

  const [userName, setUserName] = useState(null);
  const [userSurname, setUserSurname] = useState(null);
  const [userImage, setUserImage] = useState(null);
  const [userTypeMBTI, setUserTypeMBTI] = useState(null);

  const [isLoading, setIsLoading] = useState(true);
  const [userFriends, setUserFriends] = useState([]);
  const [userFriendsAmount, setUserFriendsAmount] = useState([]);

  const toProfile = async (id) => {
    navigate("/profile/" + id);
    window.location.reload();
  };


  useEffect(() => {
  
    const userId = params.userId;

    setUserId(userId);
    
    const fetchUserData = async () => {
      setIsLoading(true);

      try {
        const userInfo = await store.userInfo(userId);
        setUserName(userInfo.name);
        setUserSurname(userInfo.surname);
        setUserImage(userInfo.image);
        setUserTypeMBTI(userInfo.typeMbti);
      } catch (error) {
          console.error("Failed to fetch user data:", error);
      } finally {
          setIsLoading(false);
      }
    };

    const fetchUserFriends = async () => {
      setIsLoading(true);
      const userId = params.userId; 
      

      try {
        const friends = await store.getFriendsFull(userId);
        setUserFriends(friends);
      } catch (error) {
          console.error("Failed to fetch user data:", error);
      } finally {
          setIsLoading(false);
      }

      try {
        const friendsAmount = await store.getFriendsAmount(userId);
        setUserFriendsAmount(friendsAmount);
      } catch (error) {
          console.error("Failed to fetch user data:", error);
      } finally {
          setIsLoading(false);
      }
    };

    fetchUserData();
    fetchUserFriends();
  }, [])

  return (
    <div className='friends__wrapper'>
      <div className="friends__heading">
          <h1 className="friends__text">Друзья</h1>
          <img src={userImage} alt=" " className="friends__avatar" onClick={(e) => toProfile(userId)}/>
      </div>
      <ul className="friends__list">
        {userFriends.map(friend =>
        <li className="friends__item friend" onClick={(e) => toProfile(friend.id)}>
          <img className="friend__avatar" src={friend.picUrl} alt=""></img>
          <h2 className="friend__name">{friend.name + " " + friend.surname}</h2>
          <p className="friend__mbti">{friend.mbti}</p>
        </li>
        )}
      </ul>
    </div>
  );
};

export default Frineds;
